package dev.chiorji.config;import org.springframework.context.annotation.*;import org.springframework.web.servlet.config.annotation.*;@Configuration@EnableWebMvcpublic class WebMvcConfig implements WebMvcConfigurer {	@Override	public void addCorsMappings(CorsRegistry registry) {		registry.addMapping("/api/**")			.allowedOrigins("*")			.allowedMethods("*")			.allowedHeaders("*");	}}