package dev.publizr.config;import org.springframework.context.annotation.Configuration;import org.springframework.web.servlet.config.annotation.CorsRegistry;import org.springframework.web.servlet.config.annotation.EnableWebMvc;import org.springframework.web.servlet.config.annotation.ResourceHandlerRegistry;import org.springframework.web.servlet.config.annotation.WebMvcConfigurer;@Configuration@EnableWebMvcpublic class WebMvcConfig implements WebMvcConfigurer {	@Override	public void addResourceHandlers(ResourceHandlerRegistry registry) {		registry.addResourceHandler("swagger-ui.html")			.addResourceLocations("classpath:/META-INF/resources/");		registry.addResourceHandler("/webjars/**")			.addResourceLocations("classpath:/META-INF/resources/webjars/");	}	@Override	public void addCorsMappings(CorsRegistry registry) {		registry.addMapping("/api/**")			.allowedOrigins("http://localhost:5173/")			.allowedMethods("GET", "POST", "PUT", "DELETE", "OPTIONS", "HEAD")			.allowedHeaders("Authorization", "Content-Type", "Accept", "Origin", "X-Requested-With")			.allowCredentials(true);	}}