package dev.publizr.jwt;import com.auth0.jwt.JWT;import com.auth0.jwt.algorithms.Algorithm;import com.auth0.jwt.exceptions.JWTVerificationException;import com.auth0.jwt.interfaces.Claim;import com.auth0.jwt.interfaces.DecodedJWT;import dev.publizr.constants.Constants;import jakarta.servlet.FilterChain;import jakarta.servlet.ServletException;import jakarta.servlet.ServletRequest;import jakarta.servlet.ServletResponse;import jakarta.servlet.http.HttpServletRequest;import jakarta.servlet.http.HttpServletResponse;import org.springframework.http.HttpStatus;import org.springframework.web.filter.GenericFilterBean;import java.io.IOException;public class JWTFilter extends GenericFilterBean {	Constants constants = new Constants();	String TOKEN_ISSUER = constants.TOKEN_ISSUER;	String SECRET_KEY = constants.SECRET_KEY;	@Override	public void doFilter(ServletRequest request, ServletResponse response, FilterChain chain) throws IOException, ServletException {		HttpServletRequest httpServletRequest = (HttpServletRequest) request;		HttpServletResponse httpServletResponse = (HttpServletResponse) response;		String token = this.getBearerTokenFromRequest((HttpServletRequest) request);		try {			DecodedJWT decodedJWT = this.validateJWTToken(token);			Claim id = decodedJWT.getClaim("id");			Claim username = decodedJWT.getClaim("username");			Claim email = decodedJWT.getClaim("email");			httpServletRequest.setAttribute("id", id);			httpServletRequest.setAttribute("email", email);			httpServletRequest.setAttribute("username", username);		} catch (RuntimeException e) {			httpServletResponse.sendError(HttpStatus.FORBIDDEN.value(), "INVALID OR EXPIRED AUTHORIZATION TOKEN");			return;		}		chain.doFilter(httpServletRequest, httpServletResponse);	}	private String getBearerTokenFromRequest(HttpServletRequest request) {		var header = request.getHeader("Authorization");		if (header == null) return null;		return header.replace("Bearer ", "");	}	private DecodedJWT validateJWTToken(String jwtToken) {		try {			Algorithm algorithm = Algorithm.HMAC256(SECRET_KEY);			return JWT.require(algorithm)				.withIssuer(TOKEN_ISSUER)				.build()				.verify(jwtToken);		} catch (JWTVerificationException e) {			throw new RuntimeException(e);		}	}}